import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { fetchOrderDetails } from "../actions/orderAction";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";

class OrderDetails extends Component {
  componentWillMount() {
    this.props.fetchOrderDetails(this.props.location.state.orderId);
  }

  handleOrderClick = () => {};

  render() {
    const options = {
      page: 1,
      sizePerPage: 10,
      pageStartIndex: 1,
      paginationSize: 3,
      prePage: "Prev",
      nextPage: "Next",
      firstPage: "First",
      lastPage: "Last",
      paginationShowsTotal: this.renderShowsTotal,
      paginationPosition: "both",
      onRowClick: function(rowData, cell) {}
    };
    console.log("render");
    console.log(this.props.orderDetails);
    return (
      <div className="container">
        <BootstrapTable
          data={this.props.orderDetails}
          striped
          hover
          options={options}
          pagination={true}
        >
          <TableHeaderColumn isKey={true} dataField="id">
            id
          </TableHeaderColumn>
          <TableHeaderColumn
            dataField="product"
            dataAlign="left"
            headerAlign="left"
          >
            Description
          </TableHeaderColumn>
          <TableHeaderColumn dataField="quantity">Customer</TableHeaderColumn>
          <TableHeaderColumn
            dataField="quantity"
            dataAlign="right"
            headerAlign="right"
          >
            Date
          </TableHeaderColumn>
        </BootstrapTable>
      </div>
    );
  }
}

OrderDetails.propTypes = {
  fetchOrderDetails: PropTypes.func.isRequired,
  orderDetails: PropTypes.array.isRequired
};
const MapOrdersToState = state => ({
  orderDetails: state.orders.orderDetails
});

export default connect(
  MapOrdersToState,
  { fetchOrderDetails }
)(OrderDetails);
