import React, { Component } from "react";
import "./App.css";
import Container from "./components/container";
import { Provider } from "react-redux";
import store from "./store";
import AuthenticationContext from "azure-adal";

class App extends Component {
  state = { authContext: null };
  constructor(state) {
    super(state);
    var config = {
      instance: "https://login.microsoftonline.com/",
      tenant: "breakpointsoftware.onmicrosoft.com",
      clientId: "c386fc0f-93f3-40ba-b15b-a02b79896714",
      postLogoutRedirectUri: window.location.origin,
      cacheLocation: "localStorage" // enable this for IE, as sessionStorage does not work for localhost.
    };
    this.state.authContext = new AuthenticationContext(config);
  }

  render() {
    return (
      <Provider store={store}>
        {this.authContext.getCachedUser() ? (
          <Container />
        ) : (
          this.authContext.login()
        )}
      </Provider>
    );
  }
}

export default App;
