import React, { Component } from "react";
import AuthenticationContext from "azure-adal";
class a extends Component {
  state = {
    config: {
      instance: "https://login.microsoftonline.com/",
      tenant: "[Enter your tenant here, e.g. contoso.onmicrosoft.com]",
      clientId:
        "[Enter your client_id here, e.g. g075edef-0efa-453b-997b-de1337c29185]",
      postLogoutRedirectUri: window.location.origin,
      cacheLocation: "localStorage" // enable this for IE, as sessionStorage does not work for localhost.
    }
    // authContext
  };

  handleLogin = () => {
    console.log("handleLogin");
    debugger;
    var authContext = new a(this.state.config);
    authContext.login();
  };

  render() {
    return (
      <React.Fragment>
        <a href="#" className="navbar-brand" onClick={this.handleLogin}>
          Logout
        </a>
        <a href="#" className="navbar-brand">
          Login
        </a>
      </React.Fragment>
    );
  }
}

export default Login;
