import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { fetchOrders } from "../actions/orderAction";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";

class Orders extends Component {
  componentWillMount() {
    this.props.fetchOrders();
  }

  handleOrderClick = () => {};

  render() {
    return (
      <React.Fragment>
        <div className="container">
          <BootstrapTable data={this.props.orders} striped hover>
            <TableHeaderColumn isKey={true} dataField="id">
              id
            </TableHeaderColumn>
            <TableHeaderColumn dataField="description">
              Description
            </TableHeaderColumn>
            <TableHeaderColumn dataField="customer">Customer</TableHeaderColumn>
            <TableHeaderColumn dataField="fecha">Date </TableHeaderColumn>
            <TableHeaderColumn dataField="quantity">
              Quantity{" "}
            </TableHeaderColumn>
            <TableHeaderColumn dataField="total">Total</TableHeaderColumn>
          </BootstrapTable>
        </div>
      </React.Fragment>
    );
  }
}

Orders.propTypes = {
  fetchOrders: PropTypes.func.isRequired,
  orders: PropTypes.array.isRequired
};
const MapOrdersToState = state => ({
  orders: state.orders.items
});
export default connect(
  MapOrdersToState,
  { fetchOrders }
)(Orders);
