import { FETCH_ORDERS, FETCH_ORDER_WITH_DETAILS } from "./types";
import { apiConfig } from "./config";
export const fetchOrders = () => dispatch => {
  fetch(apiConfig.endPoint + "/orders/get")
    .then(res => res.json())
    .then(orders =>
      dispatch({
        type: FETCH_ORDERS,
        payload: orders
      })
    );
};

export const fetchFullOrder = orderId => dispatch => {
  fetch(apiConfig.endPoint + "/orders/getFullOrder/" + orderId, {
    method: "get",
    headers: new Headers({
      Authorization: "Basic " + btoa("username:password"),
      "Content-Type": "application/x-www-form-urlencoded"
    })
  })
    .then(res => res.json())
    .then(fullOrder =>
      dispatch({
        type: FETCH_ORDER_WITH_DETAILS,
        payload: fullOrder
      })
    );
};
