import { FETCH_ORDERS } from "../actions/types";

const initialState = {
  items: []
};
export default function(state = initialState, action) {
  switch (action.type) {
    case FETCH_ORDERS:
      return {
        ...state,
        orders: action.payload
      };

    default:
      return state;
  }
}
