import { FETCH_ORDERS, FETCH_ORDER_DETAIL } from "./types";

export const fetchOrders = () => dispatch => {
  fetch("https://localhost:44360/v1/orders/get")
    .then(res => res.json())
    .then(orders =>
      dispatch({
        type: FETCH_ORDERS,
        payload: orders
      })
    );
};

export const fetchOrderDetails = () => dispatch => {
  fetch("https://localhost:44360/v1/orders/getdetail")
    .then(res => res.json())
    .then(orderDetail =>
      dispatch({
        type: FETCH_ORDERS,
        payload: orderDetail
      })
    );
};
