import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { fetchFullOrder } from "../actions/orderAction";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";

class OrderDetails extends Component {
  componentWillMount() {
    this.props.fetchFullOrder(this.props.location.state.orderId);
  }

  render() {
    const options = {
      page: 1,
      sizePerPage: [
        {
          text: "10",
          value: "10"
        }
      ],
      sizePerPage: 10,
      pageStartIndex: 1,
      paginationSize: 3,
      prePage: "Prev",
      nextPage: "Next",
      firstPage: "First",
      lastPage: "Last",
      paginationShowsTotal: this.renderShowsTotal,
      paginationPosition: "both"
    };

    return (
      <div className="container">
        <h4>Order</h4>
        <div className="panel panel-default">
          <div className="panel-body">
            <div className="container">
              <div className="row">
                <div className="col-6">
                  <b>Description:&nbsp;</b>
                  {this.props.selectedOrder.description}
                </div>
                <div className="col-6">
                  <b>Date:&nbsp;</b>
                  {this.props.selectedOrder.date}
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-6">
                <b> Quantity:</b>
                &nbsp;
                {this.props.selectedOrder.quantity}
              </div>
              <div className="col-6">
                <b>Total:&nbsp;</b>
                {this.props.selectedOrder.total}
              </div>
            </div>
          </div>
        </div>
        <h4>Detail</h4>
        <div className="panel panel-default">
          <div className=" panel-body">
            <BootstrapTable
              data={this.props.selectedOrder.details}
              striped
              hover
              options={options}
              pagination={true}
            >
              <TableHeaderColumn isKey={true} hidden dataField="id">
                id
              </TableHeaderColumn>
              <TableHeaderColumn
                dataField="product"
                dataAlign="left"
                headerAlign="left"
              >
                Product
              </TableHeaderColumn>
              <TableHeaderColumn
                dataField="unitaryPrice"
                dataAlign="right"
                headerAlign="right"
              >
                Unitary Price
              </TableHeaderColumn>
              <TableHeaderColumn
                dataField="quantity"
                dataAlign="right"
                headerAlign="right"
              >
                Quantity
              </TableHeaderColumn>
              <TableHeaderColumn
                dataField="subtotal"
                dataAlign="right"
                headerAlign="right"
              >
                Subtotal
              </TableHeaderColumn>
            </BootstrapTable>
          </div>
        </div>
      </div>
    );
  }
}

OrderDetails.propTypes = {
  fetchFullOrder: PropTypes.func.isRequired,
  selectedOrder: PropTypes.object.isRequired
};
const MapOrdersToState = state => ({
  selectedOrder: state.orderReducer.selectedOrder
});

export default connect(
  MapOrdersToState,
  { fetchFullOrder }
)(OrderDetails);
