import { FETCH_ORDERS, FETCH_ORDER_WITH_DETAILS } from "./types";

export const fetchOrders = () => dispatch => {
  fetch("https://localhost:44360/v1/orders/get")
    .then(res => res.json())
    .then(orders =>
      dispatch({
        type: FETCH_ORDERS,
        payload: orders
      })
    );
};

export const fetchFullOrder = orderId => dispatch => {
  fetch("https://localhost:44360/v1/orders/getFullOrder/" + orderId)
    .then(res => res.json())
    .then(orderDetails =>
      dispatch({
        type: FETCH_ORDER_WITH_DETAILS,
        payload: orderDetails
      })
    );
};
