import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { fetchOrderDetails } from "../actions/orderAction";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";

class OrderDetails extends Component {
  componentWillMount() {}

  handleOrderClick = () => {};

  render() {
    const options = {
      page: 1,
      sizePerPageList: [
        {
          text: "5",
          value: 5
        },
        {
          text: "10",
          value: 10
        },
        {
          text: "All",
          value: this.props.orderDetails.length
        }
      ],
      sizePerPage: 10,
      pageStartIndex: 1,
      paginationSize: 3,
      prePage: "Prev",
      nextPage: "Next",
      firstPage: "First",
      lastPage: "Last",
      paginationShowsTotal: this.renderShowsTotal,
      paginationPosition: "both",
      onRowClick: function(rowData, cell) {}
    };
    return (
      <div className="container">
        <BootstrapTable
          data={this.props.orderDetail}
          striped
          hover
          options={options}
          pagination={true}
        >
          <TableHeaderColumn isKey={true} dataField="id">
            id
          </TableHeaderColumn>
          <TableHeaderColumn
            dataField="description"
            dataAlign="left"
            headerAlign="left"
          >
            Description
          </TableHeaderColumn>
          <TableHeaderColumn dataField="customer">Customer</TableHeaderColumn>
          <TableHeaderColumn
            dataField="fecha"
            dataAlign="left"
            headerAlign="left"
          >
            Date
          </TableHeaderColumn>
          <TableHeaderColumn
            dataField="quantity"
            dataAlign="right"
            headerAlign="right"
          >
            Quantity
          </TableHeaderColumn>
          <TableHeaderColumn
            dataField="total"
            dataAlign="right"
            headerAlign="right"
          >
            Total
          </TableHeaderColumn>
        </BootstrapTable>
      </div>
    );
  }
}

OrderDetails.propTypes = {
  fetchOrderDetails: PropTypes.func.isRequired,
  orderDetails: PropTypes.array.isRequired,
  order: PropTypes.object.isRequired
};
const MapOrdersToState = state => ({
  orderDetails: [],
  order: this.props.location.state.order
});

export default connect(
  MapOrdersToState,
  { fetchOrderDetails }
)(OrderDetails);
