import React, { Component } from "react";
import AuthenticationContext from "azure-adal";
class Login extends Component {
  state = {
    config: {
      instance: "https://login.microsoftonline.com/",
      tenant: "breakpointsoftware.onmicrosoft.com",
      clientId: "c386fc0f-93f3-40ba-b15b-a02b79896714",
      postLogoutRedirectUri: window.location.origin,
      cacheLocation: "localStorage" // enable this for IE, as sessionStorage does not work for localhost.
    }
  };

  handleLogin = () => {
    var authContext = new AuthenticationContext(this.state.config);
    authContext.login();
  };

  render() {
    return (
      <React.Fragment>
        <a href="#" className="navbar-brand" onClick={this.handleLogin}>
          Logout
        </a>
        <a href="#" className="navbar-brand">
          Login
        </a>
      </React.Fragment>
    );
  }
}

export default Login;
