import { FETCH_ORDERS } from "./types";

export const fetchOrders = () => dispatch => {
  fetch("https://localhost:44360/v1/orders/get")
    .then(res => res.json())
    .then(orders =>
      dispatch({
        type: FETCH_ORDERS,
        payload: orders
      })
    );
};

export const fetchOrderDetails = () => dispatch => {
  fetch("https://localhost:44360/v1/orders/getdetail")
    .then(res => res.json())
    .then(orderDetails =>
      dispatch({
        type: FETCH_ORDERS,
        payload: orders
      })
    );
};

