import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { fetchOrders } from "../actions/orderAction";

class Orders extends Component {
  componentWillMount() {
    this.props.fetchOrders();
  }

  handleOrderClick = () => {};

  rowStyleFormat = (row, rowIdx) => {
    return { backgroundColor: rowIdx % 2 === 0 ? "red" : "blue" };
  };

  render() {
    const items = this.props.orders.map((o, i) => (
      <div className="row" key={o.id} style={rowStyleFormat(o, i)}>
        <div className="col-sm">{o.description}</div>
        <div className="col-sm">{o.customer.description}</div>
        <div className="col-sm">{o.fecha.toLocaleString()}</div>
        <div className="col-sm">{o.total}</div>
        <div className="col-sm">{o.quantity}</div>
      </div>
    ));
    return (
      <React.Fragment>
        <div className="container">{items}</div>
      </React.Fragment>
    );
  }
}

Orders.propTypes = {
  fetchOrders: PropTypes.func.isRequired,
  orders: PropTypes.array.isRequired
};
const MapOrdersToState = state => ({
  orders: state.orders.items
});
export default connect(
  MapOrdersToState,
  { fetchOrders }
)(Orders);
