import { FETCH_ORDERS, FETCH_ORDER_WITH_DETAILS } from "./types";
import { apiConfig } from "./config";
import { adalApiFetch } from "../adal/config";
export const fetchOrders = () => dispatch => {
  adalApiFetch(fetch, apiConfig.endPoint + "/orders/get", {
    mode: "no-cors",
    method: "GET"
  })
    // fetch(apiConfig.endPoint + "/orders/get", {
    //   mode: "no-cors",
    //   method: "GET",
    //   headers: apiConfig.getHeader()
    // })
    .then(res => res.json(), err => console.error(err))
    .then(orders =>
      dispatch({
        type: FETCH_ORDERS,
        payload: orders
      })
    );
};

export const fetchFullOrder = orderId => dispatch => {
  fetch(
    apiConfig.endPoint + "/orders/getFullOrder/" + orderId,
    {
      mode: "no-cors",
      method: "GET",
      headers: apiConfig.getHeader()
    },
    ""
  )
    .then(res => res.json())
    .then(fullOrder =>
      dispatch({
        type: FETCH_ORDER_WITH_DETAILS,
        payload: fullOrder
      })
    );
};
